    +desktop
    +mobile                

</template>

<script>
import navMixin from '@/mixins/navMixin'

export default {
    mixins: [navMixin],
    props: {
        isMobile: {
            type: Boolean,
            required: true
        }
    },
    methods: {
        toggleMobileMenu() {
            this.$emit('toggle-mobile-menu');
        }     
    },
    computed: {
               isMobileMix() {
            if (window.matchMedia("(max-width:960px)").matches) {
                return true;
              } else {
                return false;
              }        
    }    
    }

}
</script>
