mixin arrow
    .desk

mixin both
    .about-advantages__wrap
        .about-advantages__arrow
            h2.op-800 
                span= info.title.span
                | !{info.title.main}
            img(src="~@/assets/images/about-advantages-arrow.png")
        .about-advantages__list
            each item, index in info.list                
                article
                    +svgList(index)
                    .article
                        h3.op-800= item.title
                        p.op-400= item.subtitle


section.about-advantages
    +both

</template>
